package Array;

public class LargestNum {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=10;
		int b=30;
		int c=23;
		
		if((a>b)&&(a>c))
				{
			System.out.println(a);
				}
		else if((b>a)&&(b>c))
		{
			System.out.println(b);
		}
		else 
		{
			System.out.println(c);
		}

	}

}
