package LogicalPrograms;

public class Triange {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		for(int i=0; i<=5;i++)// it will print the 5 rows
		{
			for(int j=0;j<=i;j++)// it will print the columns value
			{
				System.out.print("* ");// it will print the *
			}
			System.out.println();// it will add the new line after each execution
		}

	}

}
