package Test;

public class Run2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		for(int i=1;i<=5;i++)
		{
			for(int j=i;j<=5;j++)
			{
				System.out.print(" ");
			}
			for(int j=i;j<=5;i++)
			{
				System.out.print("* ");
			}
			System.out.println();
		}
		
				
	}

}
